import React, { useEffect, useState, useCallback } from "react";
import { Link, useHistory } from "react-router-dom";
import { Swal } from "sweetalert2";
import axios from "axios";
import AOS from "aos";

function Users() {
    const [users, setUsers] = useState([]);
    const [length, setLength] = useState(10);
    const [page, setPage] = useState([]);
    const [numPage, setNum] = useState(0);

    const [token, setToken] = useState(localStorage.getItem("token"));
    const urls = "http://127.0.0.1:8000/api";

    let history = useHistory();

    const show = (event) => {
		event.
		setLength(event.target.value);
		console.log(event.target.value);
        console.log(length);
        if (length > users.length) {
            setPage(users);
        } else {
            setPage(users.slice(numPage * length, (numPage + 1) * length));
        }
    };
    const minus = () => {
        setNum(numPage - 1);
    };
    const plus = () => {
        setNum(numPage + 1);
    };
    const getUsers = useCallback(async () => {
        let url = urls + "/users";
        let options = {
            method: "get",
            url: url,
            headers: {
                Authorization: "Bearer " + token,
                Accept: "Application/json",
            },
        };
        await axios(options).then((res) => {
            let data = res.data;
            setUsers(data);
            if (10 > data.length) {
                setPage(users);
            } else {
                setPage(data.slice(0, 10));
            }
            console.log(data);
        });
    }, []);

    const deleteUser = async (userId) => {
        const Swal = require("sweetalert2");
        Swal.fire({
            title: "Are you sure?",
            text: "You won't be able to revert this!",
            icon: "warning",
            iconColor: "#FFBB47",
            showCancelButton: true,
            confirmButtonColor: "#16537e",
            cancelButtonColor: "#d33",
            confirmButtonText: "Confirm",
        }).then(async (result) => {
            if (result.isConfirmed) {
                let url = urls + "/deleteUser/" + userId;
                let options = {
                    method: "DELETE",
                    url: url,
                    headers: {
                        Authorization: "Bearer " + token,
                        Accept: "Application/json",
                    },
                };
                let response = await axios(options);
                if (response && response.status === 200) {
                    Swal.fire({
                        title: "Deleted!",
                        text: "Your file has been deleted.",
                        icon: "success",
                        iconColor: "#3dc00c",
                    });
                    history.go("/users");
                }
            }
        });
    };

    useEffect(() => {
        getUsers();
    }, []);

    return (
        <div className="container-fluid">
            <h3 className="text-dark mb-4">Users</h3>
            <div className="card shadow">
                <div className="card-header py-3 d-flex flex-row justify-content-between align-items-center">
                    <p className="text-primary m-0 fw-bold">User Info</p>
                    <Link to={"/users/add"} className="btn btn-primary">
                        <i className="fas fa-user-plus"></i>
                    </Link>
                </div>
                <div className="card-body">
                    <div className="row">
                        <div className="col">
                            <label className="form-label">
                                Show&nbsp;&nbsp;
                            </label>
                            <select
                                className="d-inline-block form-select form-select-s selector"
								value = {length}
                                onChange={(event) => {
                                    show(event);
                                }}
                            >
                                <option value="10">10</option>
                                <option value="25">25</option>
                                <option value="50">50</option>
                                <option value="100">100</option>
                            </select>
                        </div>
                    </div>
                    <div
                        className="table-responsive table mt-2"
                        id="dataTable"
                        role="grid"
                        aria-describedby="dataTable_info"
                    >
                        <table className="table my-0" id="dataTable">
                            <thead>
                                <tr>
                                    <th>Name</th>
                                    <th>Email</th>
                                    <th>Role</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {page.map((user, id) => {
                                    return (
                                        <tr key={id}>
                                            <td>
                                                <img
                                                    className="rounded-circle me-2"
                                                    width="30"
                                                    height="30"
                                                    src="assets/img/avatars/avatar1.jpeg"
                                                />
                                                {user.firstName} {user.lastName}{" "}
                                                {user.name}
                                            </td>
                                            <td>{user.email}</td>
                                            <td>{user.role}</td>
                                            <td style={{ width: "50px" }}>
                                                <div className="col">
                                                    <a
                                                        className="btn btn-danger btn-circle"
                                                        role="button"
                                                        onClick={() =>
                                                            deleteUser(
                                                                user.user_id
                                                            )
                                                        }
                                                    >
                                                        <i className="fas fa-trash text-white"></i>
                                                    </a>
                                                </div>
                                            </td>
                                        </tr>
                                    );
                                })}
                            </tbody>
                            <tfoot>
                                <tr>
                                    <td>
                                        <strong>Name</strong>
                                    </td>
                                    <td>
                                        <strong>Email</strong>
                                    </td>
                                    <td>
                                        <strong>Role</strong>
                                    </td>
                                    <td>
                                        <strong>Action</strong>
                                    </td>
                                </tr>
                            </tfoot>
                        </table>
                    </div>
                    {page.length < users.length ? (
                        <div className="row">
                            <div className="col-md-6 align-self-center">
                                <p
                                    id="dataTable_info"
                                    className="dataTables_info"
                                    role="status"
                                    aria-live="polite"
                                >
                                    Showing 1 to {page.length} of {users.length}
                                </p>
                            </div>
                            <div className="col-md-6">
                                <nav className="d-lg-flex justify-content-lg-end dataTables_paginate paging_simple_numbers">
                                    <ul className="pagination">
                                        <li className="page-item">
                                            <a
                                                className={
                                                    "page-link" + numPage === 0
                                                        ? "disabled"
                                                        : ""
                                                }
                                                aria-label="Previous"
                                                onClick={minus}
                                                href="#"
                                            >
                                                <span aria-hidden="true">
                                                    «
                                                </span>
                                            </a>
                                        </li>

                                        {() => {
                                            for (
                                                var i = 0;
                                                i < users.length / page.length;
                                                i++
                                            ) {
                                                <li
                                                    className={
                                                        "page-item " + i ===
                                                        numPage
                                                            ? "active"
                                                            : ""
                                                    }
                                                    key={i}
                                                >
                                                    <a
                                                        className="page-link"
                                                        onClick={setNum(i)}
                                                    >
                                                        {i + 1}
                                                    </a>
                                                </li>;
                                            }
                                        }}

                                        <li className="page-item">
                                            <button
                                                className={
                                                    "page-link " + numPage ===
                                                    users.length / page.length
                                                        ? "disabled"
                                                        : ""
                                                }
                                                aria-label="Next"
                                                onClick={plus}
                                            >
                                                <span aria-hidden="true">
                                                    »
                                                </span>
                                            </button>
                                        </li>
                                    </ul>
                                </nav>
                            </div>
                        </div>
                    ) : (
                        <></>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Users;
