import React, { useEffect, useState } from "react";
import { useHistory, useParams, useLocation } from "react-router-dom";
import axios from "axios";
import AOS from "aos";
import { Link } from "react-router-dom";

function Project() {
    const [post, setPost] = useState([]);
    const [token, setToken] = useState(localStorage.getItem("token"));
    var user = localStorage.getItem("user");
    const role = localStorage.getItem("role");
    const urls = "http://127.0.0.1:8000/api";

    let history = useHistory();
    const { id } = useParams();

    const getMyProject = async () => {
        let url = urls + "/projects/" + id;
        let options = {
            method: "get",
            url: url,
            headers: {
                Authorization: "Bearer " + token,
                Accept: "application/json",
            },
        };
        response = await axios(options).then((res) => {
            let response = res.data;
            setPost(response);
            console.log();
        });
    };

    const deleteProject = async () => {
        let url = urls + "/projects/delete/" + id;
        let options = {
            method: "DELETE",
            url: url,
            headers: {
                Authorization: "Bearer " + token,
                Accept: "application/json",
            },
        };
        const Swal = require("sweetalert2");
        Swal.fire({
            title: "Are you sure?",
            text: "You won't be able to revert this!",
            icon: "warning",
            iconColor: "#FFBB47",
            showCancelButton: true,
            confirmButtonColor: "#16537e",
            cancelButtonColor: "#d33",
            confirmButtonText: "Confirm",
        }).then(async (result) => {
            if (result.isConfirmed) {
                let response = await axios(options);
                if (response && response.status === 200) {
                    Swal.fire({
                        title: "Deleted!",
                        text: "Your file has been deleted.",
                        icon: "success",
                        iconColor: "#3dc00c",
                    }).then(() => history.push("/projects/mine"));
                }
            }
        });
    };

    const archiveProject = async () => {
        let url = urls + "/projects/archive/" + id;
        let options = {
            method: "DELETE",
            url: url,
            headers: {
                Authorization: "Bearer " + token,
                Accept: "application/json",
            },
        };
        let response = await axios(options);
        if (response && response.status === 200) {
            history.go("/projects");
        }
    };

    const approveProject = async () => {
        let url = urls + "/projects/approve/" + id;
        let options = {
            method: "PUT",
            url: url,
            headers: {
                Authorization: "Bearer " + token,
                Accept: "application/json",
            },
        };
        let response = await axios(options);
        if (response && response.status === 200) {
            history.go("/projects");
        }
    };

    const editProject = async () => {
        history.go("projects/edit/" + id);
    };

    useEffect(() => {
        getMyProject();
        AOS.init();
        AOS.refresh();
    }, []);

    return (
        <div className="container-fluid margin">
            <div className="project shadow-lg">
                <div className="row">
                    <div className="col">
                        <label className="form-label project-title">
                            {post.title}
                        </label>
                        {post.state === "approved" ? (
                            <label className="form-label approved">
                                Approved
                            </label>
                        ) : post.state === "rejected" ? (
                            <label className="form-label rejected">
                                Rejected
                                <a className="rejected" href="#">
                                    ?
                                </a>
                            </label>
                        ) : (
                            <label className="form-label pendding-approval">
                                Pendidng Approval
                            </label>
                        )}
                    </div>
                    <div className="col action">
                        <a
                            className="btn btn-danger btn-circle ms-1"
                            role="button"
                            onClick={deleteProject}
                        >
                            <i className="fas fa-trash text-white"></i>
                        </a>
                        <a
                            className="btn btn-info btn-circle ms-1"
                            role="button"
                            onClick={editProject}
                        >
                            <i className="fas fa-edit text-white"></i>
                        </a>
                        <a
                            className="btn btn-warning btn-circle ms-1"
                            role="button"
                            onClick={archiveProject}
                        >
                            <i className="fas fa-archive text-white"></i>
                        </a>
                        <a
                            className="btn btn-success btn-circle ms-1"
                            role="button"
                            onClick={approveProject}
                        >
                            <i className="fas fa-check text-white"></i>
                        </a>
                    </div>
                </div>
                <div className="row">
                    <div className="col">
                        <label className="col-form-label">
                            jfdljfjsdkfjlslf
                            {/* Created by {post.author.name} on {post.dateOfCreation} */}
                        </label>
                    </div>
                </div>
                <div className="row">
                    <div className="col">
                        <label className="col-form-label">
                            Level {post.level}
                        </label>
                    </div>
                </div>
                <div className="row">
                    <div className="col">
                        <label className="form-label title">Summary</label>
                        <p>
                            {post.summary}
                            <br />
                        </p>
                    </div>
                </div>
                <div className="row">
                    <div className="col">
                        <label className="form-label title">Description</label>
                        <p>
                            {post.description}
                            <br />
                        </p>
                    </div>
                </div>
                <div className="row">
                    <div className="col">
                        <label className="form-label title">Keywords</label>
                        <ul className="list-group keywords">
                            {post.keywords?.map((keyword) => {
                                return (
                                    <div>
                                        <li className="list-group-item keyword d-flex justify-content-between">
                                            <span> {keyword}</span>
                                        </li>
                                    </div>
                                );
                            })}
                        </ul>
                    </div>
                </div>
                <div className="row">
                    <label className="form-label title">Attachment</label>
                    <div className="col file-review">
                        <label className="form-label">
                            Here you can find the project presentation sheet
                        </label>
                        <div>
                            <img
                                className="pdf-style"
                                src={
                                    window.location.origin +
                                    "/assets/img/pdf-24.svg"
                                }
                            />
                            <a
                                href={
                                    "http://127.0.0.1:8000/files/" + post.file
                                }
                                target="_blank"
                                download
                            >
                                Download the file here
                            </a>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Project;
