import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";
import AOS from "aos";

function Navbar() {
	const [post, setPost] = useState([]);
	const [searchfield, setField] = useState("");
	let data = {
		searchfield,
	};

	let history = useHistory();

	const search = async () => {
		let url = "http://127.0.0.1:8000/api/projects/search";
		let options = {
			method: "get",
			url: url,
			data: data,
		};

		let response = await axios(options);
		if (response.status === 200) {
			history.go("/search/results");
		}
	};

	const getNotifications = async () => {
		let url = "http://127.0.0.1:8000/api/users/notifications";
		let options = {
			method: "get",
			url: url,
		};
		await axios(options).then((res) => {
			let response = res.data;
			setPost(response.projects);
			console.log(response);
		});
	};
	useEffect(() => {
		getNotifications();
		AOS.init();
		AOS.refresh();
	}, []);

	return (
		<nav className="navbar navbar-light navbar-expand bg-white shadow mb-4 topbar static-top">
			<div className="container-fluid">
				<button
					className="btn btn-link d-md-none rounded-circle me-3"
					id="sidebarToggleTop"
					type="button"
				>
					<i className="fas fa-bars"></i>
				</button>
				<form className="d-none d-sm-inline-block me-auto ms-md-3 my-2 my-md-0 mw-100 navbar-search">
					<div className="input-group">
						<input
							className="bg-light form-control border-0 small"
							type="text"
							placeholder="Search for ..."
							onChange={(event) => {
								setField(event.target.value);
							}}
						/>
						<button
							className="btn btn-primary py-0"
							type="button"
							onClick={search}
						>
							<i className="fas fa-search"></i>
						</button>
					</div>
				</form>
				<ul className="navbar-nav flex-nowrap ms-auto">
					<li className="nav-item dropdown d-sm-none no-arrow">
						<a
							className="dropdown-toggle nav-link"
							aria-expanded="false"
							data-bs-toggle="dropdown"
							href="#"
						>
							<i className="fas fa-search"></i>
						</a>
						<div
							className="dropdown-menu dropdown-menu-end p-3 animated--grow-in"
							aria-labelledby="searchDropdown"
						>
							<form className="me-auto navbar-search w-100">
								<div className="input-group">
									<input
										className="bg-light form-control border-0 small"
										type="text"
										placeholder="Search for ..."
										onChange={(event) => {
											setField(event.target.value);
										}}
									/>
									<div className="input-group-append">
										<button
											className="btn btn-primary py-0"
											type="button"
											onClick={search}
										>
											<i className="fas fa-search"></i>
										</button>
									</div>
								</div>
							</form>
						</div>
					</li>
					<li className="nav-item dropdown no-arrow mx-1">
						<div className="nav-item dropdown no-arrow">
							<a
								className="dropdown-toggle nav-link"
								aria-expanded="false"
								data-bs-toggle="dropdown"
								href="#"
							>
								{() => {
									if (post.length != 0) {
										<span className="badge bg-danger badge-counter">
											{post.length}+
										</span>;
									}
								}}

								<i className="fas fa-bell fa-fw"></i>
							</a>
							<div className="dropdown-menu dropdown-menu-end dropdown-list animated--grow-in">
								<h6 className="dropdown-header">alerts center</h6>
								{() => {
									if (post.length === 0) {
										return (
											<a
												className="dropdown-item d-flex align-items-center"
												href="#"
											>
												<div>
													<p>
														There is no notification to display
													</p>
												</div>
											</a>
										);
									}
								}}
								{post.notifications.map((notification, index) => {
									return (
										<a
											className="dropdown-item d-flex align-items-center"
											href="#"
										>
											<div className="me-3">
												{() => {
													if (notification.type === "warning") {
														<div className="bg-warning icon-circle">
															<i className="fas fa-exclamation-triangle text-white"></i>
														</div>;
													}
												}}
												{() => {
													if (notification.type === "primary") {
														<div className="bg-primary icon-circle">
															<i className="fas fa-file-alt text-white"></i>
														</div>;
													}
												}}
											</div>
											<div>
												<span className="small text-gray-500">
													{notification.date}
												</span>
												<p>{notification.content}</p>
											</div>
										</a>
									);
								})}
							</div>
						</div>
					</li>
					<div className="d-none d-sm-block topbar-divider"></div>
					<li className="nav-item dropdown no-arrow">
						<div className="nav-item dropdown no-arrow">
							<a
								className="dropdown-toggle nav-link"
								aria-expanded="false"
								data-bs-toggle="dropdown"
								href="#"
							>
								<span className="d-none d-lg-inline me-2 text-gray-600 small">
									{user.firstName + " " + user.lastName}
								</span>
								<img
									className="border rounded-circle img-profile"
									src={user.avatar}
								/>
							</a>
							<div className="dropdown-menu shadow dropdown-menu-end animated--grow-in">
								<a className="dropdown-item" href="#">
									<i className="fas fa-user fa-sm fa-fw me-2 text-gray-400"></i>
									&nbsp;Profile
								</a>
								<a className="dropdown-item" href="#">
									<i className="fas fa-cogs fa-sm fa-fw me-2 text-gray-400"></i>
									&nbsp;Settings
								</a>
								<div className="dropdown-divider"></div>
								<a className="dropdown-item" href="#">
									<i className="fas fa-sign-out-alt fa-sm fa-fw me-2 text-gray-400"></i>
									&nbsp;Logout
								</a>
							</div>
						</div>
					</li>
				</ul>
			</div>
		</nav>
	);
}

export default Navbar;
