import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";
import AOS from "aos";

function MyProjects() {
	const [posts, setPost] = useState([]);
	const [token, setToken] = useState(localStorage.getItem("token"));
	const urls = "http://127.0.0.1:8000/api"

	let history = useHistory();
	const goTo = async (id) => {
		history.go("/projects/" + id);
	};

	const getMyProjects = async () => {
		let url = urls+ "/theme";
		let options = {
			method: "get",
			url: url,
			headers: {
				Authorization:
					"Bearer " + token,
				Accept: "Application/json",
			},
		};
		console.log("options");
		await axios(options).then((res) => {
			let data = res.data;
			setPost(data);
		console.log(data);
		});
	};
	useEffect(() => {
		getMyProjects();
		AOS.init();
		AOS.refresh();
	}, []);

	return (
		<div className="col">
			{() => {
				if (posts.length === 0) {
					return <h1>Oops! there is no project to display</h1>;
				}
			}}
			{posts.map((post, id) => {
				return (
					<div key={id} className="row">
						<div className="col">
							<div className="project shadow-lg">
								<label className="form-label project-title">
									{post.title}
								</label>
								{
									(post.state === "approved") ? (<span style={{ fontSize:".8rem" ,color:"black"}}>There is no keyword added yet</span>)
									: (post.sta)

								}
								<label className={"form-label " +(post.state==="approved")? "approved":((post.state==="rejected")? "rejected":"pendding-approval")}>
									{post.state}
								</label>
								<p>
									{post.summary}
									<br />
								</p>
								<div className="row">
									<div className="col">
										<label className="form-label title">
											Tools and Tags
										</label>
										<ul className="list-group keywords">
											{post.keywords.map((keyword, index) => {
												return (
													<li
														key={index}
														className="list-group-item keyword"
													>
														<span>{keyword}</span>
													</li>
												);
											})}
										</ul>
										<button
											className="btn btn-primary more-details-btn"
											type="button"
											onClick={() => {
												goTo(post.id);
											}}
										>
											More details
										</button>
									</div>
								</div>
							</div>
						</div>
					</div>
				);
			})}
		</div>
	);
}

export default MyProjects;
