import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";
import AOS from "aos";

function Dashboard() {
    const [posts, setPost] = useState([]);
    const [token, setToken] = useState(localStorage.getItem("token"));
    const [role,setRole] =useState(localStorage.getItem("role"));
    const urls = "http://127.0.0.1:8000/api";

    let history = useHistory();
    const goTo = async (id) => {
        history.push("/projects/" + id);
    };


    const getall = async () => {
        let url = urls + "/projects/all";
        let options = {
            method: "get",
            url: url,
            headers: {
                Authorization: "Bearer " + token,
                Accept: "Application/json",
            },
        };
        console.log("options");
        await axios(options).then((res) => {
            let data = res.data;
            setPost(data);
        });
    };
    const getProjects = async () => {
        let url = urls + "/projects/";
        let options = {
            method: "get",
            url: url,
            headers: {
                Authorization: "Bearer " + token,
                Accept: "Application/json",
            },
        };
        console.log("options");
        await axios(options).then((res) => {
            let data = res.data;
            setPost(data);
        });
    };
    useEffect(() => {
        let t = localStorage.getItem("token");
        console.log(t);
        if (t!==null) {
            if (role==="admin"){
                getall();
            }else{

            getProjects();
            }
            AOS.init();
            AOS.refresh();
        } else {
            history.push("/login");
        }
    }, []);

    return (
        <>
            <h3 className="text-dark mb-4">All Suggested Projects</h3>
            <div className="col">
                {() => {
                    if (posts.length === 0) {
                        return <h1>Oops! there is no project to display</h1>;
                    }
                }}
                {posts.map((post, id) => {
                    return (
                        <div key={id} className="row">
                            <div className="col">
                                <div className="project shadow-lg">
                                    <label className="form-label project-title">
                                        {post.title}
                                    </label>

                                    <div className="row">
                                        <div className="col">
                                            <label className="form-label title">
                                                keywords
                                            </label>
                                            <ul className="list-group keywords">
                                                {post.keywords.map(
                                                    (keyword, index) => {
                                                        return (
                                                            <li
                                                                key={index}
                                                                className="list-group-item keyword"
                                                            >
                                                                <span>
                                                                    {keyword}
                                                                </span>
                                                            </li>
                                                        );
                                                    }
                                                )}
                                            </ul>
                                            <button
                                                className="btn btn-primary more-details-btn"
                                                type="button"
                                                onClick={() => {
                                                    goTo(post.id);
                                                }}
                                            >
                                                More details
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    );
                })}
            </div>
        </>
    );
}

export default Dashboard;
