          let response = await axios(options);
        if (response && response.status === 200) {Swal.fire({
            title: "Password changed",
            icon: "success",
            iconColor: "#3dc00c",
        }).then(async ()=>{


          history.push("/profile");
        });}
        } catch (error) {
          Swal.fire({
            title: "Oops somthing hapened!",
            text: "Please verify your input and try again",
            icon: "error",
        });
        };
      };
