import React, { useEffect, useState, useCallback } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";
import AOS from "aos";

function Users() {
  const [posts, setPosts] = useState([]);

  let history = useHistory();
  const goTo = async (id) => {
    history.go("/projects/" + id);
  };

  const getUsers = useCallback(async () => {
    let url = "http://192.168.43.151:8000/api/users";
    let options = {
      method: "get",
      url: url,
      headers: {
        Authorization: "Bearer " + "1|Z7GR8hWZ9qaSRPwzgP3ZYezESqGqUQWJ2gPY3nvW",
        Accept: "Application/json",
      },
    };
    await axios(options).then((res) => {
      let data = res.data;
      setPosts(data);
      console.log(data);
    });
  }, []);

  const deleteUser = async (userId) => {
    let url = "http://192.168.43.151:8000/api/deleteUser/" + userId;
    let options = {
      method: "DELETE",
      url: url,
      headers: {
        Authorization: "Bearer " + "4|BTMyBywDPrpv6W2mfSlhFfKN4SRLmD9TbYtU3MUb",
        Accept: "Application/json",
      },
    };
    let response = await axios(options);
    if (response && response.status === 200) {
      history.go("/users");
    }
  };

  useEffect(() => {
    getUsers();
    openModal = (event) => {
        document.body.classList.add('bg-image justify-c');
        this.setState({ showModal: true });
      }
      hideModal = (event) => {
        document.body.classList.remove('modal-open');
        this.setState({ showModal: false });
      }
    AOS.init();
    AOS.refresh();
  }, [getUsers]);
  return (
    <div className="container ">
      <div className="row justify-content-center">
        <div className="col-lg-6 d-none d-lg-flex">
          <div className="flex-grow-1 bg-login-image">
            <img className="logo" src={window.location.origin + "/assets/img/EsiProject-logo.png"} />
          </div>
        </div>
        <div className="col-lg-6 bg-white content-center">
          <div className="p-5">
            <div className="text-center">
              <h4 className="text-dark mb-4">Get Started!</h4>
            </div>
            <form className="user">
              <div className="mb-3">
                <label className="form-label">Email</label>
                <input
                  className="form-control form-control-user"
                  type="email"
                  id="username-1"
                  placeholder="Enter Your Email..."
                  name="email"
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Password</label>
                <input
                  className="form-control form-control-user"
                  type="password"
                  id="exampleInputPassword-1"
                  placeholder="Enter Your Password..."
                  name="password"
                  required
                />
              </div>


              <button className="btn btn-primary d-block btn-user" type="submit">
                Login
              </button>
              <hr />
            </form>
            <div className="text-center">
              <a className="small" href="forgot-password.html">
                Forgot Password?
              </a>
            </div>
            <div className="text-center"></div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Users;
