import react, { components } from "react";
import axios from "axios";
import { useEffect, useState } from "react";
import { useHistory } from "react-router";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";

import { Swal } from "sweetalert2";

import jQuery from "jquery";

function Profile() {
    const Swal = require("sweetalert2");
    const [loading, setLoading] = useState(true);
    const [fileSelected, setfileSelected] = useState(null);
    const history = useHistory();
    const [emailErr, setEmailErr] = useState(false);
    const [passwordErr, setPasswordErr] = useState(false);
    const [userInput, setUser] = useState([]);
    const [error, setError] = useState([]);
    const [password, setPassword] = useState("");

    const [token, setToken] = useState(localStorage.getItem("token"));
    const SelectFile = (e) => {
        setfileSelected(e.target.files[0]);
    };
    const getprofile = async () => {
        const category_id = this.props.match.params.id;
        await axios.get("url").then((res) => {
            if (res.data.status === 200) {
                setUser(res.data.category);
            } else if (res.data.status === 404) {
                const Swal = require("sweetalert2");
                Swal.fire({
                    title: "ERROR",
                    text: "You won't be able to revert this!",
                    icon: "error",
                });
                history.push("/profile");
            }
        });
    };

    useEffect(() => {
        getprofile();
    }, []);

    const handleInput = (e) => {
        e.persists();
        setUser({ ...userInput, [e.target.name]: e.target.value });
    };
    /*function emailHandler(e) {
      let item = e.target.value;
      let pattern =
          /[a-zA-Z0-9]+[\.]?([a-zA-Z0-9]+)?[\@][a-z\-]{3,9}[\.][a-z]{2,5}/g;
      if (pattern.test(item)) {
          setEmailErr(false);
      } else {
          setEmailErr(true);
      }
  }*/
    function handlePassword(event) {
        let item = event.target.value;
        console.log(item);
        if (item.length > 8) {
            setPasswordErr(false);
            setPassword(event.target.value);
        } else {
            setPasswordErr(true);
        }
    }
    const changePassword = async (event) => {
        event.preventDefault();
        const data = {
            password,
        };
        let url =
            "http://127.0.0.1:8000/api/changepassword/" +
            localStorage.getItem("user");
        let options = {
            method: "POST",
            data,
            url: url,
            headers: {
                Authorization: "Bearer " + token,
                Accept: "Application/json",
            },
        };
        try {
            let response = await axios(options);
            if (response && response.status === 200) {
                Swal.fire({
                    title: "Password changed",
                    icon: "success",
                    iconColor: "#3dc00c",
                }).then(async () => {
                    history.push("/profile");
                });
            }
        } catch (error) {
            Swal.fire({
                title: "Oops somthing hapened!",
                text: "Please verify your input and try again",
                icon: "error",
            });
        }
    };
    const updateUser = (e) => {
        e.preventDefault();
        const category_id = this.props.match.params.id;
        /*axios.put(url,data).then(
      res=>{
          if (res.data.status===200){
              swal("Success",res.data.message,"success");
              setError([]);
          }

          else if(res.data.status===422){
              setError(response.data.errors);
              swal("All fields ar mandetory","error");
          }
          else if(res.data.status===404){
              swal("Error",res.data.message,"error");
              history.push('/pageerreur')
          }
      }
  );*/
    };
    if (false) {
        return <h4>Loading Edit Category...</h4>;
    }

    jQuery(function ($) {
        var bodyContainer = document.querySelector(".body-container");
        // for sticky nav to work
        // overflow is visible by default in desktop modes, but it's hidden in mobile to prevent horizontal scrollbars
        bodyContainer.style.overflow = "visible";

        var profileTabs = document.querySelector(".sticky-nav");
        profileTabs.addEventListener("sticky-change", function (e) {
            // when tabs become sticky, add is-stuck class (see @page-style.css)
            this.classList.toggle("is-stuck", e.detail.isSticky);
        });

        $("#sidebar")
            .on("show.ace.sidebar", function () {
                if (this.classList.contains("sidebar-push")) {
                    bodyContainer.style.overflow = ""; // so we don't have body scrollbars
                }
            })
            .on("hide.ace.sidebar", function () {
                if (this.classList.contains("sidebar-push")) {
                    bodyContainer.style.overflow = "visible"; //so we have our sticky nav back
                }
            });

        //  var _animate = !AceApp.Util.isReducedMotion()

        // progress chart showing user skills
        var skillData = [74, 68, 50];
        var skillColor = ["green-m1", "purple-m1", "primary-m1"];
        var skillTextColor = ["secondary-d3", "secondary-d3", "secondary-d3"];
        var skillName = ["HTML &amp; CSS", "Angular", "Backend"];

        for (var p = 0; p < skillData.length; p++) {
            var canvas = $(
                '<div class="pos-rel m-2 text-center text-' +
                    skillColor[p] +
                    '" style="max-width: 30%;">\
            <canvas height="100" width="100"></canvas>\
            <span class="position-center text-85 text-600 text-' +
                    skillTextColor[p] +
                    '">' +
                    skillName[p] +
                    "</span>\
        </div>"
            );

            $("#skill-chart").append(canvas);
            var color = canvas.css("color");
            canvas = canvas.find("canvas");

            var config = {
                type: "doughnut",
                data: {
                    datasets: [
                        {
                            data: [skillData[p], 100 - skillData[p]],
                            backgroundColor: [color, "#dbe7ed"],
                            hoverBackgroundColor: [color, "#dbe7ed"],
                            borderWidth: 0,
                        },
                    ],
                },

                options: {
                    responsive: true,
                    cutoutPercentage: 88,
                    rotation: Math.PI * 0.5,
                    legend: {
                        display: false,
                    },
                    animation: {
                        //animateScale: true,
                        //  duration: _animate ? 500 : 0,
                    },
                    tooltips: {
                        enabled: false,
                    },
                },
            };

            // new Chart(canvas.get(0), config)
        }

        //////////////////////////////
        // file input in edit profile

        $("#id-field0").aceFileInput({
            style: "drop",
            droppable: true,

            container: "border-1 border-dashed brc-grey-m4 brc-h-warning-m1",

            placeholderClass: "text-125 text-600 text-grey-l1 my-2",
            placeholderText: "Drop profile image or click to choose",
            placeholderIcon:
                '<i class="fa fa-user fa-3x text-green-m3 my-2"></i>',

            thumbnail: "large",

            allowExt: "gif|jpg|jpeg|png|webp|svg",
        });
    });

    return (
        <div className="page-content container container-plus">
            <div className="row mt-2 mt-md-4">
                {/* the left side profile picture and other info */}
                <div className="col-12 col-md-4">
                    <div className="card acard">
                        <div className="card-body">
                            <span className="d-none position-tl mt-2 pt-3px">
                                <span className="text-white bgc-blue-d1 ml-2 radius-b-1 py-2 px-2">
                                    <i className="fa fa-star" />
                                </span>
                            </span>
                            <div className="d-flex flex-column py-3 px-lg-3 justify-content-center align-items-center">
                                <div className="pos-rel">
                                    <img
                                        alt="Profile image"
                                        src="assets/img/avatar/avatar5.png"
                                        className="radius-round bord1er-2 brc-warning-m1"
                                    />
                                    <span className=" position-tr bgc-success p-1 radius-round border-2 brc-white mt-2px mr-2px" />
                                </div>
                                <div className="text-center mt-2">
                                    a
                                    <h5 className="text-130 text-dark-m3">
                                        Amy Smith
                                    </h5>
                                    <span className="d-none badge bgc-orange-l3 text-orange-d3 pt-2px pb-1 text-85 radius-round px-25 border-1 brc-orange-m3">
                                        pro
                                    </span>
                                </div>
                            </div>
                            {/* /.d-flex */}
                        </div>
                        {/* /.card-body */}
                    </div>
                    {/* /.card */}
                </div>
                {/* .col */}
                {/* the right side profile tabs */}
                <div className="col-12 col-md-8 mt-3 mt-md-0">
                    <div className="card dcard h-100">
                        <div className="card-body p-0">
                            <div className="sticky-nav">
                                <div className="position-tr w-100 border-t-4 brc-blue-m2 radius-2 d-md-none" />
                                <ul
                                    id="profile-tabs"
                                    className="nav nav-tabs-scroll is-scrollable nav-tabs nav-tabs-simple p-1px pl-25 bgc-secondary-l4 border-b-1 brc-dark-l3 radius-t-1"
                                    role="tablist"
                                >
                                    <li className="nav-item mr-2 mr-lg-3">
                                        <a
                                            className="d-style nav-link active px-2 py-35 brc-primary-tp1"
                                            data-toggle="tab"
                                            href="#profile-tab-overview"
                                            role="tab"
                                            aria-controls="profile-tab-overview"
                                            aria-selected="true"
                                        >
                                            <span className="d-n-active text-dark-l1">
                                                1. Overview
                                            </span>
                                            <span className="d-active text-dark-m3">
                                                1. Overview
                                            </span>
                                        </a>
                                    </li>
                                    <li className="nav-item mr-2 mr-lg-3">
                                        <a
                                            className="d-style nav-link px-2 py-35 brc-primary-tp1"
                                            data-toggle="tab"
                                            href="#profile-tab-edit"
                                            role="tab"
                                            aria-controls="profile-tab-edit"
                                            aria-selected="false"
                                        >
