import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";
import AOS from "aos";

function AddProject() {
	const [title, setTitle] = useState("");
	const [summary, setSummary] = useState("");
	const [description, setDescription] = useState("");
	const [keywords, setKeywords] = useState([]);
	const [file, setFile] = useState(null);
	const [keyword, setKeyword] = useState("");
	const [level, setLevel] = useState(null);
	const [levels, setLevels] = useState([]);

	const addKeyword = async () => {
		let list = keywords;
		list.push(keyword);
		setKeywords(list);
		setKeyword("");
	};

	let history = useHistory();

	const addProject = async () => {
		const data = {
			title,
			summary,
			description,
			keywords,
			file,
			level,
		};
		let url = "http://127.0.0.1:8000/api/projects/add";
		let options = {
			method: "post",
			url: url,
			data: data,
		};
		let response = await axios(options);
		if (response.status === 200) {
			history.replace("/");
		}
	};

	const getLevels = async () => {
		let url = "http://192.168.143.151:8000/api/levels/";
		let options = {
			method: "get",
			url: url,
		};
		await axios(options).then((res) => {
			let data = res.data;
			setLevels(data);
		});
	};
	const getKeywords = async () => {
		let url = "http://192.168.143.151:8000/api/levels/get/all";
		let options = {
			method: "get",
			url: url,
		};
		await axios(options).then((res) => {
			let data = res.data;
			setKeywords(data);
		});
	};

	useEffect(() => {
		getLevels();
		AOS.init();
		AOS.refresh();
	}, []);

	return (
		<div class="card shadow-lg o-hidden border-0 my-5 margins">
			<div class="card-body p-0">
				<div class="row">POST
					<div class="col project-form">
						<div class="p-5">
							<div class="text-center">
								<h4 class="text-dark mb-4">Add New Project!</h4>
								<form className="user" action="addProject">
									<div className="row mb-3">
										<label className="form-label">Title</label>
										<input
											id="exampleFirstName-1"
											className="form-control form-control-user "
											type="text"
											placeholder="Enter the title of the project..."
											name="title"

										/>
									</div>
									<div class="col">
										<div class="mb-3">
											<label className="form-label">Level</label>
											<select
												class="form-select"
												onChange={(event) => {
													setLevel(event.target.value);
												}}
											>
												<optgroup label="All levels">
													{levels.map((level, levelId) => {
														return (
															<option value={levelId}>
																level.value
															</option>
														);
													})}
												</optgroup>
											</select>
										</div>
									</div>

									<div className="row">
										<div className="col">
											<div className="row">
												<label className="form-label title">
													Tools and Tags
												</label>
												<ul className="list-group keywords">
													{keywords.map((keyword, keywordId) => {
														return (
															<li
																key={keywordId}
																className="list-group-item keyword"
															>
																<span>{keyword}</span>
															</li>
														);
													})}
												</ul>
											</div>
											<div className="row">
												<input
													className="form-control form-control-user"
													type="text"
													placeholder="Add a tag"
													style={{
														width: " 100%",
														textAlign: "left",
														margin: " 10px",
														height: "60px",
														fontSize: "larger",
													}}
													name="keyword"
													onChange={(event) => {
														setKeyword(event.target.value);
													}}
												/>
												<button
													className="btn btn-primary"
													type="button"
													style={{
														width: "100px",
														borderRadius: "50px",
														height: "60px",
														margin: "10px",
													}}
													onClick={addKeyword}
												>
													Add
												</button>
											</div>
										</div>
									</div>
									<div className="row mb-3">
										<label className="form-label">summary</label>
										<textarea
											className="form-control form-control-user text-area"
											placeholder="Write here a short review..."
											name="summary"
											onChange={(event) => {
												setSummary(event.target.value);
											}}
										></textarea>
									</div>
									<div className="row mb-3">
										<label className="form-label">Description</label>
										<textarea
											className="form-control form-control-user text-area"
											placeholder="Write what is the project about..."
											name="description"
											onChange={(event) => {
												setDescription(event.target.value);
											}}
										></textarea>
									</div>
									<div className="row mb-3">
										<input
											className="form-control"
											type="file"
											onChange={(event) => {
												setFile(event.target.value);
											}}
										/>
									</div>
									<div
										className="row mb-3"
										style={{
											display: "flex",
											flexDirection: "row-reverse",
										}}
									>
										<button
											className="btn btn-primary d-block add-btn"
											type="submit"
											style={{
												textAlign: "center",
												fontSize: "larger",
											}}
										>
											Create new project
										</button>
									</div>
								</form>
							</div>
						</div>
					</div>
				</div>
			</div>
		</div>
	);
}

export default AddProject;
